INSERT INTO `poem` (`id`, `title`, `date`, `subtitle`, `content`, `camera_ready_content`, `annotation`) VALUES
(1, '夕雪', '2005-01-01', '', '玄凜幽風起 霾靄逝天光\r\n霫霫入瀝泣 雲霨見神煌\r\n霡霂紛然落 霜露寒內傷\r\n氤氳窮冥暮 霙作雨仿徨', '玄凜幽風起 霾靄逝天光\r\n霫霫入瀝泣 雲霨見神煌\r\n霡霂紛然落 霜露寒內傷\r\n氤氳窮冥暮 霙作雨仿徨', ''),
(2, '無題·塵緣', '2005-01-01', '', '天晚楊糝忽作雪\r\n下渚輕舟逐水煙\r\n無花無酒春將老\r\n劍死琴銷了塵緣', '天晚楊糝忽作雪\r\n下渚輕舟逐水煙\r\n無花無酒春將老\r\n劍死琴銷了塵緣', ''),
(3, '火宵之月', '2006-11-23', '', '火煙隨銷淼\n離宵淚月纖\n千光逝流年\n幻水夢穎天\n紫冥何惘惘\n紛香徒芊芊\n殘雪拚飛盡\n漏署俟霞翩', '火煙隨銷淼\n離宵淚月纖\n千光逝流年\n幻水夢穎天\n紫冥何惘惘\n紛香徒芊芊\n殘雪拚飛盡\n漏署俟霞翩', '火宵の月 オリジナル・サウンドトラック'),
(4, '醒夜占星', '2005-01-01', '', '四侐倚孤松\n燭淚影重重\n撫弦雲倏渰\n散發夜始空\n獨立歎風晚\n躇行瞲草新\n徒然數氳暮\n惔星泣紫穹', '四侐倚孤松\n燭淚影重重\n撫弦雲倏渰\n散發夜始空\n獨立歎風晚\n躇行瞲草新\n徒然數氳暮\n惔星泣紫穹', ''),
(5, '臨餐', '2005-01-01', '', '仿徨望香起\n躑躅舉觴杯\n零目落山珍\n飄搖見海味\n亂箸指天罡\n棄盞嗔錢貴\n穿腸雖酒肉\n人間有真味', '仿徨望香起\n躑躅舉觴杯\n零目落山珍\n飄搖見海味\n亂箸指天罡\n棄盞嗔錢貴\n穿腸雖酒肉\n人間有真味', ''),
(6, '古風·憶荊軻', '2005-01-01', '', '盡日下汶水\n狂歌上岱宗\n拂劍分光曄\n泣舄望雲重\n忽聞易水歌\n獵獵有風棲\n蕭蕭去不返\n淬匕擊庭銅\n炎星亂史天\n何苦歎名窮\n浩蕩南征去\n萬裏義如風', '盡日下汶水\n狂歌上岱宗\n拂劍分光曄\n泣舄望雲重\n忽聞易水歌\n獵獵有風棲\n蕭蕭去不返\n淬匕擊庭銅\n炎星亂史天\n何苦歎名窮\n浩蕩南征去\n萬裏義如風', ''),
(7, '讀書', '2005-01-01', '', '撫紙覺風悲\r\n彈燈憶花淚\r\n風雪一頁過\r\n黯然解誰味', '撫紙覺風悲\r\n彈燈憶花淚\r\n風雪一頁過\r\n黯然解誰味', ''),
(8, '古風·代拜星月', '2005-01-01', '', '挽鬢留風冷\n持樽拜星月\n亂步棄紫金\n倒冠望神闕\n長嘯散雲壑\n惶惶見光曄\n人世當寂寞\n朝花夕多謝\n幾日乘黃鶴\n馮虛奔仙界\n茫茫冥窮暮\n吾星光如瀉', '挽鬢留風冷 持樽拜星月 亂步棄紫金 倒冠望神闕 長嘯散雲壑 惶惶見光曄 人世當寂寞 朝花夕多謝 幾日乘黃鶴 馮虛奔仙界 茫茫冥窮暮 吾星光如瀉', ''),
(9, '古風·春末', '2005-01-01', '', '伏幾忽忘性\n春末看花飛\n騁意放四海\n終倚上崔巍\n山木有蕭瑟\n連風至海內\n棄屐無餘羈\n安桀反來路\n來世未可度\n去往皆焚灰\n願逝蓬萊去\n萬里不復歸', '伏幾忽忘性\n春末看花飛\n騁意放四海\n終倚上崔巍\n山木有蕭瑟\n連風至海內\n棄屐無餘羈\n安桀反來路\n來世未可度\n去往皆焚灰\n願逝蓬萊去\n萬里不復歸', ''),
(10, '古風·西溪', '2005-01-01', '', '移舟西塘上\n日影泛水深\n翩翩夢遠跡\n幽幽忘故城\n汀上動蘭芷\n沙裏漾菱荇\n馳目黃芳燦\n花期不可期\n日來桃源渺\n遇此複忘情\n垂身風溪上\n寥寥釣無心', '移舟西塘上\n日影泛水深\n翩翩夢遠跡\n淼淼忘故城\n汀上動蘭芷\n沙裏漾菱荇\n馳目黃芳燦\n花期不可期\n日來桃源渺\n遇此複忘情\n垂身風溪上\n寥寥釣無心', ''),
(11, '古風·遊夜', '2005-01-01', '', '倚月望天宮\n光移花亂影\n攬風忘袂冷\n憑雲到北坡\n長步看新草\n小坐數寒星\n撫發知涼夜\n懶起照水鏡\n華燈應已遠\n近野無村人\n忽聞幽谷凜\n無心啟寒琴\n去日當已矣\n來世未可知\n且扣玉郎簿\n朝起夢太清', '倚月望天宮 光移花亂影 攬風忘袂冷 憑雲到北坡 長步看新草 小坐數寒星 撫發知涼夜 懶起照水鏡 華燈應已遠 近野無村人 忽聞幽谷凜 無心啟寒琴 去日當已矣 來世未可知 且扣玉郎簿 朝起夢太清', ''),
(12, '詠柏', '2005-01-01', '', '瑟瑟一叢柏\n零落幾何枝\n亂葉向天動\n繁根撫密石\n冷月曾相凜\n炙日又將灼\n冉冉未敢逝\n風起笑夢癡\n', '瑟瑟一叢柏 零落幾何枝 亂葉向天動 繁根撫密石 冷月曾相凜 炙日又將灼 冉冉未敢逝 風起笑夢癡', ''),
(13, '邀月', '2005-01-01', '', '春來樽前常俟月\n幕簾半卷碧雲斜\n白日靄霧遠春去\n黃昏烟火近人家\n平林歸鳥啼寂寞\n淺水游魚息平暇\n舉酒欲飲邀姮娥\n可憐蟾宮無春花', '春來樽前常俟月 幕簾半卷碧雲斜 白日靄霧遠春去 黃昏烟火近人家 平林歸鳥啼寂寞 淺水游魚息平暇 舉酒欲飲邀姮娥 可憐蟾宮無春花', ''),
(14, '憶君影', '2005-01-01', '', '君憶花間酒\n年來是分離\n對鏡歎風老\n提簪忘發稀\n昔觴光常滿\n今箸難為食\n春來萬將苒\n參商會有期', '君憶花間酒 年來是分離 對鏡歎風老 提簪忘發稀 昔觴光常滿 今箸難為食 春來萬將苒 參商會有期', ''),
(15, '春望', '2005-01-01', '', '盡日恨冬雪\n春來路忽迷\n野草沒失徑\n幽花隱繁蔭\n灞橋懶折柳\n長亭倦送雲\n倚門忘世意\n千里烟川平', '盡日恨冬雪\n春來路忽迷\n野草沒失徑\n幽花隱繁蔭\n灞橋懶折柳\n長亭倦送雲\n倚門忘世意\n千里烟川平', ''),
(16, '清嘯', '2005-01-01', '', '年來野徑蹤難覓\n獨立高崖袖滿風\n撫劍鳴琴聽秋水\n彈燈譜奕落春榮\n憑窗滅意千山遠\n倚榻泯情萬雲平\n且赴花間一壺酒\n長嘯廣寄送青空', '年來野徑蹤難覓\n獨立高崖滿袖風\n撫劍鳴琴聽逝水\n彈燈譜奕落春紅\n憑窗恣意千山遠\n倚榻薄情萬籟窮\n且赴花間一壺酒\n長嘯廣寄送青空', ''),
(17, '冰煌雪舞', '2005-01-01', '誌幽城幻劍錄', '夢回啓寒窗 曳影獨幢幢\n冷蕭瑟懷骨 悲憐憫冬霜\n雪舞浮光起 冰煌楚天長\n倉皇忘世凜 長嗟夜未央\n', '夢回啓寒窗 曳影獨幢幢 冷蕭瑟懷骨 悲憐憫冬霜 雪舞浮光起 冰煌楚天長 倉皇忘世凜 長嗟夜未央', ''),
(18, '春雲', '2005-01-01', '', '閑閑碧空上\n車馬何去來\n分光斂真跡\n風散棄形骸\n俯望春山好\n仰首見露臺\n今夕眄四海\n明朝送雨來', '閑閑碧空上 車馬何去來 分光斂真跡 風散棄形骸 俯望春山好 仰首見露臺 今夕眄四海 明朝送雨來', ''),
(19, '百花谷', '2005-01-01', '', '空山已寂人未跡\r\n落英無情天有晴\r\n亂步芳菲懶回顧\r\n風聲起處忽聞君', '空山已寂人未跡\n落英無情天有晴\n亂步芳菲懶回顧\n風聲起處忽聞君', ''),
(20, '飄塵', '2005-01-01', '', '坐忘湖邊好\n來去恨陽斜\n散穀待群鳥\n垂針戲魚蝦\n山青雲不雨\n水碧風無暇\n天光午後懶\n林氣日夕佳\n靜倚柴門老\n留翠煮名茶\n把盞代清酒\n歸醉共繁花', '坐忘湖邊好\n來去恨陽斜\n散穀待群鳥\n垂針戲魚蝦\n山青雲不雨\n水碧風無暇\n天光午後懶\n林氣日夕佳\n靜倚柴門老\n留翠煮名茶\n把盞代清酒\n歸醉共繁花', ''),
(21, '無題·打油', '2005-01-01', '', '長風倏起九天寒\n門外樓頭人初散\n把酒邀天天不應\n棄盞踟躇影零亂\n狂雲盡掠月蒼冷\n舉手摘星明還暗\n蒿草斜沉芳菲過\n一任嵐討湖東畔', '長風倏起九天寒 門外樓頭人初散 把酒邀天天不應 棄盞踟躇影零亂 狂雲盡掠月蒼冷 舉手摘星明還暗 蒿草斜沉芳菲過 一任嵐討湖東畔', ''),
(22, '冬夜飲醉·學時應制', '2002-01-01', '', '攬袂泛靈水，褰裳望紫薇\n回車駕四野，謝屐倚崔嵬\n崖殘映月冷，天高盡雁飛\n長風起華鬢，霜冷凍蛾眉\n舉手天星遠，俯身擷芳菲\n兩盞三月飲，一壺轉清輝\n影亂且狂舞，癲歌任冠危\n濕吾素青衫，長餘陸離佩\n留月懶鞠水，戀酒毋貪杯\n喪志豈非福，弃輯安可悲？\n援琴發清商，音響吟『式微』\n忽憤紫冥頂，白鹿安且在！\n指劍西南隅，天狼光已微\n初陽日漸遠，斗柄夜逐回\n朝華既已披，夕秀未可振？\n依稀謝前世，飄然忘今歸', '攬袂泛靈水 褰裳望紫薇\n回車駕四野 謝屐倚崔嵬\n崖殘映月冷 天高盡雁飛\n長風起華鬢 霜冷凍蛾眉\n舉手天星遠 俯身擷芳菲\n兩盞三月飲 一壺轉清輝\n影亂且狂舞 癲歌任冠危\n濕吾素青衫 長餘陸離佩\n留月懶鞠水 戀酒毋貪杯\n喪志豈非福 弃輯安可悲\n援琴發清商 音響吟『式微』\n忽憤紫冥頂 白鹿安且在\n指劍西南隅 天狼光已微\n初陽日漸遠 斗柄夜逐回\n朝華既已披 夕秀未可振\n依稀謝前世 飄然忘今歸', ''),
(23, '桃花溪', '2005-01-01', '', '風卷殘簫落英飛\r\n泛盡青溪折又回\r\n斜陽微透暮煙重\r\n醉掩清愁不需歸', '風卷殘簫落英飛\r\n泛盡青溪折又回\r\n斜陽微透暮煙重\r\n醉掩清愁不需歸', ''),
(24, '思君二首·其一', '2005-01-01', '', '寂寂人初定\r\n落葉滿秋原\r\n霜繁暮山泣\r\n月惘夜雲翩\r\n舊箋傷心語\r\n新裳斷腸褰\r\n青光十二轉\r\n離散又一年', '寂寂人初定\n落葉滿秋原\n霜繁暮山泣\n月惘夜雲翩\n舊箋傷心語\n新裳斷腸褰\n青光十二轉\n離散又一年', ''),
(25, '思君二首·其二', '2005-01-01', '', '疏雲漏殘光\r\n低江有亂航\r\n倚樓幻風雨\r\n憑闌作參商\r\n青燈彈且落\r\n寒琴抹複傷\r\n年年但鵲夜\r\n向箋費泪行', '疏雲漏殘光\n低江有亂航\n倚樓幻風雨\n憑闌作參商\n青燈彈且落\n寒琴抹複傷\n年年但鵲夜\n向箋費泪行', ''),
(26, '宴寂', '2005-01-01', '', '盡宴將何往\r\n若影逝蹁躚\r\n漸字成淒冷\r\n逐夜轉纏綿\r\n思遍璃泪結\r\n恨極青絲雪\r\n煢煢西南立\r\n送雲徒遠遷', '盡宴將何往\n若影逝蹁躚\n漸字成淒冷\n逐夜轉纏綿\n思遍璃泪結\n恨極青絲雪\n煢煢西南立\n送雲徒遠遷', ''),
(27, '夜泛秋湖', '2004-10-01', '', '秋旻多落雲\r\n平野將霧升\r\n漫花零入夜\r\n纖月散成紋\r\n小舟亂一頃\r\n弱酒醉三生\r\n何當枕來路\r\n釋數霜發增', '秋旻多落雲\n平野將霧升\n漫花零入夜\n纖月散成紋\n小舟亂一頃\n弱酒醉三生\n何當枕來路\n釋數霜發增', ''),
(28, '無題·傷雲期', '2004-10-01', '', '雁字回時凄南浦\r\n小霰分杯欲飲無\r\n雲期隨謝花零走\r\n淡看飛煙馮凜湖', '雁字回時凄南浦\r\n小霰分杯欲飲無\r\n雲期隨謝花零走\r\n淡看飛煙馮凜湖', ''),
(29, '北念友人', '2004-10-01', '', '紫艶香殘日愈秋\r\n紅箋淚盡字逐愁\r\n憑欄一望空恨水\r\n徒赴孤帆苦北流', '紫艶香殘日愈秋\r\n紅箋淚盡字成愁\r\n憑欄一望空江水\r\n徒赴孤帆苦北流', ''),
(30, '亂彈', '2005-05-28', '', '他年松下獨醉去 \n現世忽如此局殘 \n仰吹五嶽流雲散 \n俯逐三江凜水湍 \n欲摘明月秋未中 \n方濟滄海興複闌 \n何日殿前奏君上 \n請為冊與遊仙官', '他年松下獨醉去  現世忽如此局殘  仰吹五嶽流雲散  俯逐三江凜水湍  欲摘明月秋未中  方濟滄海興複闌  何日殿前奏君上  請為冊與遊仙官', ''),
(31, '夏望·歎', '2004-07-01', '', '蹙眉空坐冤 \n疑鏡老容顏 \n陰蘩固燦色 \n陽景逐虛煙 \n熏風總浮雀 \n灼天遍落雲 \n惟恨紅塵陌 \n往來無聚年', '蹙眉空坐冤疑鏡老容顏陰蘩固燦色陽景逐虛煙熏風總浮雀灼天遍落雲惟恨紅塵陌往來無聚年', ''),
(32, '無題·道癲', '2004-10-01', '', '獨對寒燈思徒矣\n褰裳一笑竟他年\n雕闌遍倚望雲老\n畫船空臥聽雨眠\n三潭弱水幾多月\n一頃西湖無限天\n殄盡金樽拼一醉\n奈由術序任誰仙！', '獨對寒燈思徒矣 褰裳一笑竟他年 雕闌遍倚望雲老 畫船空臥聽雨眠 三潭弱水幾多月 一頃西湖無限天 殄盡金樽拼一醉 奈由術序任誰仙！', ''),
(33, '古風·快意人間', '2005-07-27', '', '日來雲期皆罔淼\n游思禦劍滿燎原\n欲挽遲陽餘霞綺\n將歌大漠泠月纖\n複問人世空幾許\n弦亂清商硯寡言\n昆侖自此無完玉\n蓬萊素由尚何仙\n徒夢乘風移星渚\n亂步紫薇轉青冥\n神奔霧騎馮汶水\n心射靈馳俯華川\n驚起四顧淒迷夜\n塵煙奈落世猶艱\n長嗟大千精靈界\n凡心一念戀勢權\n惘羨松喬虛生術\n何如快意老人間', '日來雲期皆罔淼 游思禦劍滿燎原 欲挽遲陽餘霞綺 將歌大漠泠月纖 複問人世空幾許 弦亂清商硯寡言 昆侖自此無完玉 蓬萊素由尚何仙 徒夢乘風移星渚 亂步紫薇轉青冥 神奔霧騎馮汶水 心射靈馳俯華川 驚起四顧淒迷夜 塵煙奈落世猶艱 長嗟大千精靈界 凡心一念戀勢權 惘羨松喬虛生術 何如快意老人間', ''),
(34, '無月', '2005-09-17', '中秋不至，憐月凄清，故事皆非，傷年永逝作', '世塵笑積竟十載\n拂却余哀夢難成\n祭夏離風多半老\n悲秋商月幾殘生\n天宮靈氛縱猶轉\n地界清輝尚何存\n且盡金樽虛一笑\n故里顛作宦游人 ', '世塵笑積竟十載 拂却余哀夢難成 祭夏離風多半老 悲秋商月幾殘生 天宮靈氛縱猶轉 地界清輝尚何存 且盡金樽虛一笑 故里顛作宦游人 ', ''),
(35, '亂', '2006-03-13', '', '孤月皓雲台\n三山行安在\n把盞扣青冥\n舉劍問蓬萊\n海夜蒼波渺\n浮生堪愁載\n千風哭且號\n南征路已衰？\n暝色滿幽夢\n乘鵬逝東隅\n憤彼清靈界\n民生不吾哀\n此身本無牽\n何苦羈形骸\n塗炭自由命\n安為逸方外？\n胡爲絕寰去\n恁其相殺卻？\n世情空幾許\n卷袖倏複來\n悲弦盡日掃\n清音終無還\n忍顧蒼生老\n兀自滌塵埃\n青陽逐煙惘\n鏡裡鬢忽白', '孤月皓雲台 三山行安在 把盞扣青冥 舉劍問蓬萊 海夜蒼波渺 浮生堪愁載 千風哭且號 南征路已衰？ 暝色滿幽夢 乘鵬逝東隅 憤彼清靈界 民生不吾哀 此身本無牽 何苦羈形骸 塗炭自由命 安為逸方外？ 胡爲絕寰去 恁其相殺卻？ 世情空幾許 卷袖倏複來 悲弦盡日掃 清音終無還 忍顧蒼生老 兀自滌塵埃 青陽逐煙惘 鏡裡鬢忽白', ''),
